                userVotes[proposal.negativeVotes.id][_from] == 0,
                ERROR_ALREADY_NEGATIVE_VOTED
            );
            votes = proposal.positiveVotes;
        } else {
            require(
                userVotes[proposal.positiveVotes.id][_from] == 0,
                ERROR_ALREADY_POSITIVE_VOTED
            );
            votes = proposal.negativeVotes;
